<?php
// This script creates the missing product-placeholder.png
$directory = __DIR__ . '/assets/images';

// Check if directory exists, if not create it
if (!is_dir($directory)) {
    mkdir($directory, 0755, true);
}

// Base64 encoded simple placeholder image
$base64Image = '';

// Decode and save the image
$imageData = base64_decode($base64Image);
file_put_contents($directory . '/product-placeholder.png', $imageData);

echo "Product placeholder image created successfully!";
?>
